package day02;
//赋值和比较运算符
public class day02_02 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int x = 3;//把右边值赋值给左边
		System.out.println(x);
		//+=  -=  *=  /=  %=
		
		x+=4;//把左右两边的和付给左边 X = X + 4；
//		以上同理。
//		比较运算符：
//		1、比较运算符的结果都是boolean型，也就是要么是true，要么是false；
//		2、比较运算符“==”不能误写成“=”


	}

}
