package day03;
//for循环
public class Day03_02 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		/*
		for(初始化表达式;循环条件表达式;循环语句后的操作表达式)
		{
			执行语句;
		}
		*/
			for(int x = 0;x<3 ;x++)
			{
				System.out.println("x="+x);
			}
			System.out.println("Game Over!");
			
			//while
			int y = 0;
			while(y<3)
			{
				System.out.println("y="+y);
				y++;
			}
			System.out.println("Game Over!");


	}

}
